        if OK(CurDeep): #前面符合条件=>继续深一层Try
            if CurDeep == MaxDeep:
                print(List[1:])
                # break # 全排列
                continue
            Try(CurDeep+1) # 没到最后一层
    List[CurDeep] = 0

def OK(CurDeep):
    s = set(List[1:CurDeep+1])
    if len(s) == CurDeep:
        return True
    else:
        return False

